// Extraindo dados do n√≥
const inputData = $json["body"] || {}; // Obt√©m o corpo do input de dados

// Fun√ß√£o para capitalizar nomes
function capitalizeName(fullName) {
    if (!fullName || typeof fullName !== 'string') {
        return { fname: '', lname: '', fullname: '' };
    }

    const lowerCaseWords = ['da', 'de', 'do', 'di', 'du'];
    const words = fullName
        .toLowerCase()
        .split(' ')
        .map((word, index) => {
            if (lowerCaseWords.includes(word) && index !== 0) {
                return word;
            }
            return word.charAt(0).toUpperCase() + word.slice(1);
        });

    const fname = words.shift() || '';
    const lname = words.join(' ');

    return { fname, lname, fullname: `${fname} ${lname}`.trim() };
}

// Fun√ß√£o para formatar email
function formatEmail(email) {
    return email ? email.toLowerCase().trim() : '';
}

// Tratando telefone
function formatPhone(phone) {
    if (!phone || typeof phone !== 'string') return '';

    const telefoneLimpo = phone.replace(/\D/g, ""); // Remove caracteres n√£o num√©ricos
    const DDI = '55'; // C√≥digo do Brasil
    const DDD = telefoneLimpo.slice(0, 2); // Primeiros dois d√≠gitos como DDD
    const numero = telefoneLimpo.slice(-8); // √öltimos 8 d√≠gitos para o n√∫mero

    // Ajusta n√∫mero final com base no DDD
    if (parseInt(DDD) > 28) {
        return `${DDI}${DDD}${telefoneLimpo.slice(-8)}`; // Assume 8 d√≠gitos para n√∫meros com DDD > 28
    } else {
        return `${DDI}${DDD}${telefoneLimpo.slice(-9)}`; // Assume 9 d√≠gitos para n√∫meros com DDD <= 28
    }
}

// Tratamento de campos espec√≠ficos
const formattedNames = capitalizeName(inputData.contact_name || '');
const formattedEmail = formatEmail(inputData.contact_email || '');
const formattedPhone = formatPhone(inputData.contact_phone || '');
const formattedInstagram = (inputData.contact_instagram || '').replace('@', '').trim();

// Construindo o objeto final com os campos tratados e mantendo todos os demais
const result = {
    ...inputData, // Inclui todos os campos do input
    fname: formattedNames.fname, // Primeiro nome formatado
    lname: formattedNames.lname, // Sobrenome formatado
    fullname: formattedNames.fullname, // Nome completo formatado
    email: formattedEmail, // Email formatado
    whatsapp: formattedPhone, // WhatsApp formatado
    instagram: formattedInstagram, // Instagram formatado
};

// Ajustando estrutura do retorno para o n8n
return [{ json: result }];

