// Função para obter a data atual menos 15 dias no formato brasileiro (dia/mês/ano)
function getDataMenos15Dias() {
  const dataAtual = new Date();
  dataAtual.setDate(dataAtual.getDate() - 15);
  const dia = dataAtual.getDate().toString().padStart(2, '0');
  const mes = (dataAtual.getMonth() + 1).toString().padStart(2, '0'); // +1 porque o mês começa em 0
  const ano = dataAtual.getFullYear();
  return `${ano}-${mes}-${dia}`;
}

// Função para formatação de usernames do Instagram/site
function formatarInstagram(input) {
  if (input) {
    // Verificar se é um site
    const siteRegex = /^(http|https):\/\/(.*)\.(.*)$/;
    if (siteRegex.test(input)) {
      // Se for um site, retornar o input sem formatação
      return input;
    } else {
      // Remover espaços em branco extras e o símbolo "@"
      const usernameSemEspacosOuArroba = input.trim().replace(/@/g, '');

      // Converter para minúsculas
      const usernameFormatado = usernameSemEspacosOuArroba.toLowerCase();

      return usernameFormatado;
    }
  }
  return '';
}


function formatarTelefone(telefone) {
  const telefoneLimpo = telefone.replace(/[^\d]/g, '');

  let ddi;
  let ddd;
  let telefoneFormatado;

  // Verificar se o telefone começa com "55" antes de formatá-lo
  if (telefoneLimpo.startsWith('55')) {
    ddi = telefoneLimpo.slice(0, 2);
    ddd = telefoneLimpo.slice(2, 4);
    if (parseInt(ddd, 10) > 28) {
      telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-8);
    } else {
      telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-9);
    }
  } else {
    ddi = '55';
    ddd = telefoneLimpo.slice(0, 2);
    if (parseInt(ddd, 10) > 28) {
      telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-8);
    } else {
      telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-9);
    }
  }

  return telefoneFormatado;
}

// Função para extrair as UTMs do objeto JSON
function extractUTMs(json) {
  const utmParams = {};

  function searchUTMs(obj) {
    for (const key in obj) {
      if (key.startsWith('utm_')) {
        utmParams[key] = obj[key];
      } else if (typeof obj[key] === 'object') {
        searchUTMs(obj[key]);
      }
    }
  }

  searchUTMs(json);

  return utmParams;
}

