// Função para calcular a taxa de pagamento
function getPaymentFee(payment) {
    // Verificar se os campos necessários existem
    if (payment && payment.value != null && payment.netValue != null) {
        // Calcular a taxa
        const fee = payment.value - payment.netValue;
        return fee;
    } else {
        // Se os campos não existirem, retornar null
        return null;
    }
}

// Exemplo de uso da função com as variáveis fornecidas
const payment = {
    value: $node["Asaas"].json["body"]["payment"]["value"],
    netValue: $node["Asaas"].json["body"]["payment"]["netValue"]
};

const fee = getPaymentFee(payment);

// Função para formatar números de telefone
function formatarTelefone(telefone) {
    const telefoneLimpo = telefone.replace(/[^\d]/g, '');

    let ddi;
    let ddd;
    let telefoneFormatado;

    // Verificar se o telefone começa com "55" antes de formatá-lo
    if (telefoneLimpo.startsWith('55')) {
        ddi = telefoneLimpo.slice(0, 2);
        ddd = telefoneLimpo.slice(2, 4);
        if (parseInt(ddd, 10) > 28) {
            telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-8);
        } else {
            telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-9);
        }
    } else {
        // Se o DDI for diferente de 55, manter o número como está
        telefoneFormatado = telefoneLimpo;
    }

    return telefoneFormatado;
}

// Função para formatação de nomes
function formatarNome(nome) {
    if (nome) {
        const palavras = nome.trim().split(/\s+/);

        const palavrasMinusculas = ['de', 'dos', 'da', 'e', 'do', 'das', 'di', 'du', 'o', 'a', 'u'];

        for (let i = 0; i < palavras.length; i++) {
            const palavraAtual = palavras[i].toLowerCase();
            if (i === 0 || i === palavras.length - 1 || !palavrasMinusculas.includes(palavraAtual)) {
                palavras[i] = palavraAtual.charAt(0).toUpperCase() + palavraAtual.slice(1);
            } else {
                palavras[i] = palavraAtual;
            }
        }

        return palavras.join(" ");
    }
    return '';
}

// Função para formatar nomes de usuário do Instagram
function formatarInstagram(username) {
    if (typeof username === 'string') {
        // Remover espaços em branco extras
        const usernameSemEspacos = username.trim();

        // Remover o "@" do início do username, se existir
        const usernameSemArroba = usernameSemEspacos.replace(/^@/, '');

        // Converter para minúsculas
        const usernameFormatado = usernameSemArroba.toLowerCase();

        return usernameFormatado;
    }
    return '';
}

// Obter telefone e CPF/CNPJ dos dados de entrada
const telefone = $node["Busca Cliente no Asaas"].json["phone"] || $node["Busca Cliente no Asaas"].json["mobilePhone"] || '';
const cpfCnpj = $node["Busca Cliente no Asaas"].json["cpfCnpj"] || '';
let cpfCnpjFormatado;

// Formatar CPF/CNPJ conforme necessário
if (cpfCnpj.length === 11) {
    // Formatar como CPF: XXX.XXX.XXX-XX
    cpfCnpjFormatado = cpfCnpj.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
} else if (cpfCnpj.length === 14) {
    // Formatar como CNPJ: XX.XXX.XXX/0001-XX
    cpfCnpjFormatado = cpfCnpj.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
} else {
    // Formato inválido
    cpfCnpjFormatado = "Formato inválido";
}

// Resultado final formatado
const resultado = {
    telefone: telefone,
    cpfCnpj: cpfCnpjFormatado,
    fee: fee !== null ? fee.toFixed(2) : null
};

// Retornar o resultado como um array de objetos conforme esperado pelo n8n
return [resultado];

