// Função para extrair estado, cidade e CEP de um endereço
function extrairEstadoCidadeCep(endereco) {
  if (!endereco) {
    return {
      estado: "",
      cidade: "",
      cep: ""
    };
  }

  // Definir expressões regulares para capturar estado, cidade e CEP
  const estadoRegex = /(?:\b[A-Z]{2}\b\s*(?=[-,/]))/; // Regex para capturar sigla de estado
  const cidadeRegex = /-\s*([^-,/]+?)\s*(?=[-/]|[A-Z]{2}\s+)/; // Regex para capturar nome da cidade
  const cepRegex = /(?:CEP:\s*|CEP\s+)?(\d{2}\.?\d{3}-?\d{3})|(\d{8})/; // Regex para capturar CEP

  // Inicializar variáveis para armazenar os resultados
  let estado = "";
  let cidade = "";
  let cep = "";

  // Extrair o estado
  const estadoMatch = endereco.match(estadoRegex);
  if (estadoMatch) {
    estado = estadoMatch[0].trim();
    // Mapear a sigla do estado para o nome completo
    estado = mapearSiglaEstado(estado);
  }

  // Extrair a cidade
  const cidadeMatch = endereco.match(cidadeRegex);
  if (cidadeMatch) {
    // Ajuste para tratar variações na cidade
    cidade = normalizarNomeCidade(cidadeMatch[1].trim());
  }

  // Extrair o CEP
  const cepMatch = endereco.match(cepRegex);
  if (cepMatch) {
    cep = cepMatch[1] || cepMatch[2] || '';
    // Formatar CEP para o formato XXXXX-XXX se não tiver separadores
    if (/^\d{8}$/.test(cep)) {
      cep = cep.replace(/^(\d{5})(\d{3})$/, '$1-$2');
    }
  }

  // Retornar os resultados
  return {
    estado,
    cidade,
    cep
  };
}

