function formatarCPFCNPJ(valor) {
  if (!valor) {
    return ""; // Retorna uma string vazia se o campo estiver vazio
  }

  valor = valor.toString().replace(/[^\d]/g, ''); // Certifica-se de que o valor seja uma string e remove caracteres não numéricos

  if (valor.length === 11) {
    // Se o tamanho for 11, assume que é CPF e formata como CPF
    return valor.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  } else if (valor.length === 14) {
    // Se o tamanho for 14, assume que é CNPJ e formata como CNPJ
    return valor.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, '$1.$2.$3/$4-$5');
  } else {
    // Caso contrário, retorna o valor original sem formatação
    return valor;
  }
}

function formatarCEP(valor) {
  if (!valor) {
    return ""; // Retorna uma string vazia se o campo estiver vazio
  }

  valor = valor.toString().replace(/[^\d]/g, ''); // Certifica-se de que o valor seja uma string e remove caracteres não numéricos

  if (valor.length === 8) {
    // Se o tamanho for 8, formata como CEP
    return valor.replace(/(\d{5})(\d{3})/, '$1-$2');
  } else {
    // Caso contrário, retorna o valor original sem formatação
    return valor;
  }
}

//Formatar número Internacional
function formatarTelefone(telefone) {
  const telefoneLimpo = telefone.replace(/[^\d]/g, '');

  let ddi;
  let ddd;
  let telefoneFormatado;

  // Verificar se o telefone começa com "55" antes de formatá-lo
  if (telefoneLimpo.startsWith('55')) {
    ddi = telefoneLimpo.slice(0, 2);
    ddd = telefoneLimpo.slice(2, 4);
    if (parseInt(ddd, 10) > 28) {
      telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-8);
    } else {
      telefoneFormatado = ddi + ddd + telefoneLimpo.slice(-9);
    }
  } else {
    // Se o DDI for diferente de 55, manter o número como está
    telefoneFormatado = telefoneLimpo;
  }

  return telefoneFormatado;
}


